import React, {useState} from "react";
import axios from "axios";


function App() {

  const [data, setData] = useState({})
  const [location, setLocation] = useState('')

  const url = 'https://api.openweathermap.org/data/2.5/weather?q=${location}&appid=02f12a156a768b72c0af4e67515d6eed'

const searchLocation = (event) => {
  if (event.key === 'Enter')
  axios.get(url).then((response) => {
    setData(response.data)
    console.log(response.data)
  })
  setLocation('')
}

  return (
    <div className="app">
      <div className="search">
        <input 
        value={location}
        onChange={event => setLocation(event.target.value)}
        placeholder='Enter Location'
        onKeyPress={searchLocation}
        type="text">

        </input>
      </div>
     <div className="container">
      <div className="top">
        <div className="location">
          <p>{data.name}</p>
        </div>
        <div className="temp">
          {data.main ? <h1>{data.main.temp}</h1> : null}
        </div>
        <div className="description">
          <p>Clouds</p>
        </div>
      </div>
      <div className="bottom">
        <div className="feels">
        <p>Feels like</p>
          <p className="bold">60ÂºF</p>
        </div>
        <div className="humidity">
          <p>Humidity</p>
          <p className="bold">20%</p>
        </div>
        <div className="wind">
          <p>Wind Speed</p>
          <p className="bold">5mph</p>
        </div>
      </div>
     </div>
    </div>
  );
}

//Key: 02f12a156a768b72c0af4e67515d6eed
//API Call: https://api.openweathermap.org/data/2.5/weather?lat={lat}&lon={lon}&appid={API key}
