import React, {useState} from "react";
import axios from "axios";


function App() {

  const [data, setData] = useState({})
  const [location, setLocation] = useState('')

  const url = `https://api.openweathermap.org/data/2.5/weather?q=${location}&units=imperial&appid=02f12a156a768b72c0af4e67515d6eed`

const searchLocation = (event) => {
  if (event.key === 'Enter') {
    axios.get(url).then((response) => {
      setData(response.data)
      console.log(response.data)
    })
    setLocation('')
  }
}

  return (
    <div className="app">
      <div className="search">
        <input 
        value={location}
        onChange={event => setLocation(event.target.value)}
        placeholder='Enter Location'
        onKeyDownCapture={searchLocation}
        type="text">

        </input>
      </div>
     <div className="container">
      <div className="top">
        <div className="location">
          <p>{data.name}</p>
        </div>
        <div className="temp">
          {data.main ? <h1>{data.main.temp.toFixed()}ºF</h1> : null}
        </div>
        <div className="description">
          {data.weather ? <p>{data.weather[0].main}</p> : null}
        </div>
      </div>
      <div className="bottom">
        <div className="feels">
          <p>Feels Like</p>
          {data.main ? <p className="bold">{data.main.feels_like.toFixed()}ºF</p> : null}
        </div>
        <div className="humidity">
        <p>Humidity</p>
          {data.main ? <p className="bold">{data.main.humidity}%</p> : null}
       </div>
        <div className="wind">
          <p>Wind Speed</p>
          {data.wind ? <p className="bold">{data.wind.speed} MPH</p> : null}
        </div>
      </div>
     </div>
    </div>
  );
}

//Key: 02f12a156a768b72c0af4e67515d6eed
//API Call: https://api.openweathermap.org/data/2.5/weather?lat={lat}&lon={lon}&appid={API key}
export default App;
